// import React
import React, { useEffect, useState } from 'react';

import {
    Grid,
    Link,
    Button,
    Dialog,
    DialogActions,
    DialogContentText,
    DialogTitle,
    DialogContent,
    Typography,
    FormControl,
    FormGroup,
    InputLabel,
    FormHelperText,
    Input
} from '@mui/material';
import MuiTypography from '@mui/material/Typography';
import { DataGrid } from '@mui/x-data-grid';

// project imports
import SubCard from 'ui-component/cards/SubCard';
import MainCard from 'ui-component/cards/MainCard';
import SecondaryAction from 'ui-component/cards/CardSecondaryAction';
import { gridSpacing } from 'store/constant';
import { userColumns } from 'assets/columns/gridData';

// import library
import axios from 'axios';

// ==============================|| TYPOGRAPHY ||============================== //

const UserList = () => {
    const [rows, setRows] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);

    useEffect(() => {
        const GetAllUser = async (offset, limit) => {
            try {
                const config = {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                };
                const res = await axios.post(
                    'http://localhost:3010/v1/api/tastie/admin/get-all-user',
                    {
                        limit,
                        offset
                    },
                    config
                );

                if (res.data.status) {
                    setRows(res.data.response);
                }
            } catch (error) {
                console.error('Cannot get all user', error);
            }
        };

        GetAllUser(1, 1000000);
    }, []);

    return (
        <MainCard
            title="User list"
            secondary={
                <Button disableElevation variant="contained" onClick={() => setOpenDialog(true)}>
                    Add User
                </Button>
            }
        >
            <DataGrid
                rows={rows}
                columns={userColumns}
                // pageSize={50}
                // rowsPerPageOptions={[40]}
                checkboxSelection
                disableSelectionOnClick
                getRowId={(row) => row.user_id}
                sx={{ width: '100%', height: 500 }}
                // onPageChange={(page) => alert(page)}
            />
            <div>
                <Dialog open={openDialog} onClose={() => setOpenDialog(false)} maxWidth="xl">
                    <DialogTitle id="alert-dialog-title">
                        <Typography variant="h3">Enter the user infomation</Typography>
                    </DialogTitle>
                    <DialogContent>
                        <FormControl sx={{ width: '100%' }}>
                            <FormGroup
                                sx={{
                                    width: '100%',
                                    display: 'flex',
                                    flexDirection: 'row',
                                    alignItems: 'center',
                                    justifyContent: 'space-around'
                                }}
                                row
                            >
                                <FormControl sx={{ width: '40%' }}>
                                    <InputLabel htmlFor="ecoupon_code">Ecoupon Code</InputLabel>
                                    <Input
                                        id="ecoupon_code"
                                        aria-describedby="my-helper-text"
                                        // onChange={(event) =>
                                        //     setFormData((prev) => ({
                                        //         ...prev,
                                        //         [event.target.id]: event.target.value
                                        //     }))
                                        // }
                                    />
                                </FormControl>
                                <FormControl sx={{ width: '40%' }}>
                                    <InputLabel htmlFor="ecoupon_name">Ecoupon Name</InputLabel>
                                    <Input
                                        id="ecoupon_name"
                                        aria-describedby="my-helper-text"
                                        // onChange={(event) =>
                                        //     setFormData((prev) => ({
                                        //         ...prev,
                                        //         [event.target.id]: event.target.value
                                        //     }))
                                        // }
                                    />
                                </FormControl>
                            </FormGroup>

                            <FormGroup
                                sx={{
                                    width: '100%',
                                    display: 'flex',
                                    flexDirection: 'row',
                                    alignItems: 'center',
                                    justifyContent: 'space-around',
                                    marginTop: 5
                                }}
                                row
                            >
                                <FormControl sx={{ width: '40%' }}>
                                    <InputLabel htmlFor="ecoupon_value">Ecoupon value</InputLabel>
                                    <Input
                                        id="ecoupon_value"
                                        aria-describedby="my-helper-text"
                                        // onChange={(event) =>
                                        //     setFormData((prev) => ({
                                        //         ...prev,
                                        //         [event.target.id]: parseFloat(event.target.value)
                                        //     }))
                                        // }
                                    />
                                </FormControl>
                                <FormControl sx={{ width: '40%' }}>
                                    <InputLabel htmlFor="ecoupon_description">Ecoupon description</InputLabel>
                                    <Input
                                        id="ecoupon_description"
                                        aria-describedby="my-helper-text"
                                        // onChange={(event) =>
                                        //     setFormData((prev) => ({
                                        //         ...prev,
                                        //         [event.target.id]: event.target.value
                                        //     }))
                                        // }
                                    />
                                </FormControl>
                            </FormGroup>

                            <FormGroup
                                sx={{
                                    width: '100%',
                                    display: 'flex',
                                    flexDirection: 'row',
                                    alignItems: 'center',
                                    justifyContent: 'space-around',
                                    marginTop: 5
                                }}
                                row
                            >
                                <FormControl sx={{ width: '40%' }}>
                                    <InputLabel htmlFor="min_order_value">Min order value</InputLabel>
                                    <Input
                                        id="min_order_value"
                                        aria-describedby="my-helper-text"
                                        // onChange={(event) =>
                                        //     setFormData((prev) => ({
                                        //         ...prev,
                                        //         [event.target.id]: parseFloat(event.target.value)
                                        //     }))
                                        // }
                                    />
                                </FormControl>
                                <FormControl sx={{ width: '40%' }}>
                                    <InputLabel htmlFor="max_discount_value">Max discount value</InputLabel>
                                    <Input
                                        id="max_discount_value"
                                        aria-describedby="my-helper-text"
                                        // onChange={(event) =>
                                        //     setFormData((prev) => ({
                                        //         ...prev,
                                        //         [event.target.id]: parseFloat(event.target.value)
                                        //     }))
                                        // }
                                    />
                                </FormControl>
                            </FormGroup>

                            <FormGroup
                                sx={{
                                    width: '100%',
                                    display: 'flex',
                                    flexDirection: 'row',
