// import React
import React, { useEffect, useState } from 'react';

import {
    Grid,
    Link,
    Button,
    Dialog,
    DialogActions,
    DialogContentText,
    DialogTitle,
    DialogContent,
    Typography,
    FormControl,
    FormGroup,
    InputLabel,
    FormHelperText,
    Input
} from '@mui/material';
import { useTheme } from '@mui/material/styles';
import MuiTypography from '@mui/material/Typography';
import { DataGrid } from '@mui/x-data-grid';

// project imports
import SubCard from 'ui-component/cards/SubCard';
import MainCard from 'ui-component/cards/MainCard';
import SecondaryAction from 'ui-component/cards/CardSecondaryAction';
import { gridSpacing } from 'store/constant';
import { userColumns } from 'assets/columns/gridData';

// import library
import axios from 'axios';

// ==============================|| TYPOGRAPHY ||============================== //

const UserList = () => {
    const theme = useTheme();
    const [rows, setRows] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [openNotification, setOpenNotification] = useState(false);
    const [formData, setFormData] = useState({
        email: null,
        password: null,
        phone: null,
        first_name: null,
        last_name: null,
        role: null
    });
    const [selectedUser, setSelectedUser] = useState([]);
    const [showRemoveButton, setShowRemoveButton] = useState(false);

    const GetAllUser = async (offset, limit) => {
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json'
                }
            };
            const res = await axios.post(
                'http://localhost:3010/v1/api/tastie/admin/get-all-user',
                {
                    limit,
                    offset
                },
                config
            );

            if (res.data.status) {
                setRows(res.data.response.filter((item) => item.user_status !== 2));
            }
        } catch (error) {
            console.error('Cannot get all user', error);
        }
    };

    const formatDate = (date) => {
        const d = new Date(date);
        let month = (d.getMonth() + 1).toString();
        let day = d.getDate().toString();
        const year = d.getFullYear();

        if (month.length < 2) month = `0${month}`;
        if (day.length < 2) day = `0${day}`;

        return [year, month, day].join('-'); // 2022-05-04
    };

    const handleAddUser = async (formData) => {
        try {
            const res = await axios.post('http://localhost:3010/v1/api/tastie/admin/add-user', formData);
            if (res.data.status) {
                setOpenDialog(false);
                setOpenNotification(true);
                // await GetAllUser(1, 1000000);
            }
        } catch (error) {
            console.error('Cannot add new user', error);
        }
    };

    const handleRemoveUser = async (userIdList) => {
        // this function get the array of user_id to be removed => remove multiple users
        try {
            const removeUser = async (userId) => {
                const res = await axios.put(`http://localhost:3010/v1/api/tastie/admin/remove-user/${userId}`);

                return res.data;
            };
            const queryList = [];
            userIdList.forEach((userId) => {
                queryList.push(removeUser(userId)); // get the query list of removing users
            });
            Promise.all(queryList).then((values) => {
                console.log('Response from removing users', values);
                GetAllUser(1, 10000000);
            });
        } catch (error) {
            console.error('Cannot remove users', error);
        }
    };

    useEffect(() => {
        GetAllUser(1, 1000000);
    }, []);

    useEffect(() => {
        if (selectedUser.length > 0) {
            setShowRemoveButton(true);
        } else {
            setShowRemoveButton(false);
        }
    }, [selectedUser]);

    return (
        <MainCard
            title="User list"
            secondary={
                <>
                    {showRemoveButton && (
                        <Button
                            disableElevation
                            onClick={() => handleRemoveUser(selectedUser)}
                            sx={{ marginRight: 2, backgroundColor: theme.palette.error.dark, color: 'white' }}
                        >
                            Remove User
                        </Button>
                    )}
                    <Button
                        disableElevation
                        onClick={() => setOpenDialog(true)}
                        sx={{ backgroundColor: theme.palette.primary.dark, color: 'white' }}
                    >
                        Add User
                    </Button>
                </>
            }
        >
            <DataGrid
                initialState={{
                    sorting: {
                        sortModel: [{ field: 'user_id', sort: 'desc' }] // set descending order as default
                    }
                }}
                rows={rows}
                columns={userColumns}
                // pageSize={50}
                // rowsPerPageOptions={[40]}
                checkboxSelection
                disableSelectionOnClick
                getRowId={(row) => row.user_id}
                sx={{ width: '100%', height: 500 }}
                // onPageChange={(page) => alert(page)}
                onSelectionModelChange={(userIdList, _) => {
                    console.log(userIdList);
                    setSelectedUser(userIdList);
                    // handleRemoveUser(userIdList);
                }}
            />
            <Dialog
                open={openDialog}
                onClose={() => setOpenDialog(false)}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title" sx={{ width: '100%', alignItems: 'center' }}>
                    <Typography variant="h3">Add new User</Typography>
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description" sx={{ marginRight: 20 }}>
                        Please enter all these required information for the new user
                    </DialogContentText>
                    <FormControl sx={{ width: '100%', py: 2 }}>
                        <FormGroup
                            sx={{
                                width: '100%',
                                display: 'flex',
                                flexDirection: 'row',
                                alignItems: 'center',
                                justifyContent: 'space-around'
                            }}
                            row
                        >
                            <FormControl sx={{ width: '40%' }}>
                                <InputLabel htmlFor="email">Email</InputLabel>
                                <Input
                                    id="email"
                                    aria-describedby="my-helper-text"
                                    onChange={(event) =>
                                        setFormData((prev) => ({
                                            ...prev,
                                            [event.target.id]: event.target.value
                                        }))
                                    }
                                />
                            </FormControl>
                            <FormControl sx={{ width: '40%' }}>
                                <InputLabel htmlFor="password">Password</InputLabel>
                                <Input
                                    id="password"
                                    aria-describedby="my-helper-text"
                                    type="password"
                                    onChange={(event) =>
                                        setFormData((prev) => ({
                                            ...prev,
                                            [event.target.id]: event.target.value
                                        }))
                                    }
                                />
                            </FormControl>
                        </FormGroup>

                        <FormGroup
                            sx={{
                                width: '100%',
                                display: 'flex',
                                flexDirection: 'row',
                                alignItems: 'center',
                                justifyContent: 'space-around',
                                marginTop: 5
                            }}
                            row
                        >
                            <FormControl sx={{ width: '40%' }}>
                                <InputLabel htmlFor="phone">Phone number</InputLabel>
                                <Input
                                    id="phone"
                                    aria-describedby="my-helper-text"
                                    onChange={(event) =>
                                        setFormData((prev) => ({
                                            ...prev,
                                            [event.target.id]: parseFloat(event.target.value)
                                        }))
                                    }
                                />
                            </FormControl>
                            <FormControl sx={{ width: '40%' }}>
                                <InputLabel htmlFor="first_name">First Name</InputLabel>
                                <Input
                                    id="first_name"
                                    aria-describedby="my-helper-text"
                                    onChange={(event) =>
                                        setFormData((prev) => ({
                                            ...prev,
                                            [event.target.id]: event.target.value
                                        }))
                                    }
                                />
                            </FormControl>
                        </FormGroup>

                        <FormGroup
                            sx={{
                                width: '100%',
                                display: 'flex',
                                flexDirection: 'row',
                                alignItems: 'center',
                                justifyContent: 'space-around',
                                marginTop: 5
                            }}
                            row
                        >
                            <FormControl sx={{ width: '40%' }}>
                                <InputLabel htmlFor="last_name">Last Name</InputLabel>
                                <Input
                                    id="last_name"
                                    aria-describedby="my-helper-text"
                                    onChange={(event) =>
                                        setFormData((prev) => ({
                                            ...prev,
                                            [event.target.id]: event.target.value
                                        }))
                                    }
                                />
                            </FormControl>
                            <FormControl sx={{ width: '40%' }}>
                                <InputLabel htmlFor="role">Role</InputLabel>
                                <Input
                                    id="role"
                                    aria-describedby="my-helper-text"
                                    onChange={(event) =>
                                        setFormData((prev) => ({
                                            ...prev,
                                            [event.target.id]: parseFloat(event.target.value)
                                        }))
                                    }
                                />
                            </FormControl>
                        </FormGroup>

                        {/* <Button
                                variant="contained"
                                color="primary"
                                sx={{ margin: 'auto', marginTop: 10 }}
                                onClick={(event) => {
                                    event.preventDefault();
                                    handleAddEcoupon(formData);
                                }}
                            >
                                Add Ecoupon
                            </Button> */}
                    </FormControl>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
                    <Button onClick={() => handleAddUser(formData)} autoFocus>
                        Apply
                    </Button>
                </DialogActions>
            </Dialog>
            {/* Notification dialog */}
            <Dialog
                open={openNotification}
                onClose={() => setOpenNotification(false)}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">Add new user successfully</DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">The new user has been added in the database</DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button
                        onClick={() => {
                            setOpenDialog(false);
                            GetAllUser(1, 1000000);
                        }}
                    >
                        Got it
                    </Button>
                </DialogActions>
            </Dialog>
        </MainCard>
    );
};

export default UserList;
