import React, { useEffect, useState } from 'react';

import { styled } from '@mui/material/styles';
import { Card, FormControl, FormGroup, InputLabel, FormHelperText, Input } from '@mui/material';

// project imports
import MainCard from 'ui-component/cards/MainCard';
import SecondaryAction from 'ui-component/cards/CardSecondaryAction';

// styles
const IFrameWrapper = styled('iframe')(({ theme }) => ({
    height: 'calc(100vh - 210px)',
    border: '1px solid',
    borderColor: theme.palette.primary.light
}));

// ============================|| MATERIAL ICONS ||============================ //

const AddEcoupon = () => {
    const [rows, setrows] = useState({});
    return (
        <MainCard title="Add an Ecoupon" secondary={<SecondaryAction link="https://next.material-ui.com/components/material-icons/" />}>
            <FormControl sx={{ width: '100%' }}>
                <FormGroup
                    sx={{
                        width: '100%',
                        display: 'flex',
                        flexDirection: 'row',
                        alignItems: 'center',
                        justifyContent: 'space-around'
                    }}
                    row
                >
                    <FormControl sx={{ width: '40%' }}>
                        <InputLabel htmlFor="my-input">Ecoupon Id</InputLabel>
                        <Input id="my-input" aria-describedby="my-helper-text" />
                    </FormControl>
                    <FormControl sx={{ width: '40%' }}>
                        <InputLabel htmlFor="my-input">Ecoupon Name</InputLabel>
                        <Input id="my-input" aria-describedby="my-helper-text" />
                    </FormControl>
                </FormGroup>

                <FormGroup
                    sx={{
                        width: '100%',
                        display: 'flex',
                        flexDirection: 'row',
                        alignItems: 'center',
                        justifyContent: 'space-around',
                        marginTop: 20
                    }}
                    row
                >
                    <FormControl sx={{ width: '40%' }}>
                        <InputLabel htmlFor="my-input">Ecoupon value</InputLabel>
                        <Input id="my-input" aria-describedby="my-helper-text" />
                    </FormControl>
                    <FormControl sx={{ width: '40%' }}>
                        <InputLabel htmlFor="my-input">Ecoupon description</InputLabel>
                        <Input id="my-input" aria-describedby="my-helper-text" />
                    </FormControl>
                </FormGroup>
            </FormControl>
        </MainCard>
    );
};

export default AddEcoupon;
